import{_ as l,c as a,o as i,Q as e}from"./chunks/framework.03c6a765.js";const b=JSON.parse('{"title":"前端工程化","description":"","frontmatter":{},"headers":[],"relativePath":"25-build/10-前端工程化.md","filePath":"25-build/10-前端工程化.md","lastUpdated":1703663478000}'),o={name:"25-build/10-前端工程化.md"},t=e('<h1 id="前端工程化" tabindex="-1">前端工程化 <a class="header-anchor" href="#前端工程化" aria-label="Permalink to &quot;前端工程化&quot;">​</a></h1><p>前端工程化主要关注：</p><ol><li>开发阶段</li><li>打包阶段</li><li>发布阶段</li><li>渲染阶段</li></ol><h2 id="前端优化" tabindex="-1">前端优化 <a class="header-anchor" href="#前端优化" aria-label="Permalink to &quot;前端优化&quot;">​</a></h2><p>前端优化主要是为提高 <code>打包速度</code>、<code>发布速度</code> 和 <code>渲染速度</code>，<strong>资源越小传输越快</strong>、<strong>代码越优执行越快</strong>。</p><div class="warning custom-block"><p class="custom-block-title">警告</p><p><code>要优化先分析</code>，使用合适的工具对<code>过程</code>和<code>产物</code>进行分析，确定<code>性能指标</code>和<code>优化方向</code>。</p></div><h2 id="开发阶段优化" tabindex="-1">开发阶段优化 <a class="header-anchor" href="#开发阶段优化" aria-label="Permalink to &quot;开发阶段优化&quot;">​</a></h2><ul><li>不写重复代码，提取公共模块（为打包阶段优化做铺垫）</li><li>选择合适的第三方库，避免引入无用代码（为打包阶段优化做铺垫）</li><li>提高编程水平，避免内存泄漏（为渲染阶段优化做铺垫）</li><li>拆分主副功能，异步懒加载模块（为渲染阶段优化做铺垫）</li><li>多媒体资源压缩，做响应式适配（为渲染阶段优化做铺垫）</li><li>使用合适的数据结构和算法提高执行效率（为渲染阶段优化做铺垫）</li><li>使用高性能动画和 DOM 操作，避免页面回流和重绘（为渲染阶段优化做铺垫）</li></ul><h2 id="打包阶段优化" tabindex="-1">打包阶段优化 <a class="header-anchor" href="#打包阶段优化" aria-label="Permalink to &quot;打包阶段优化&quot;">​</a></h2><ul><li>增量构建支持，减小非首次构建的源码大小。</li><li>提取免打包代码(第三方库)，减小源码大小。</li><li>模块拆分，避免模块代码重复构建。</li><li>配置按需加载模块，减少无用代码。</li></ul><h2 id="发布阶段" tabindex="-1">发布阶段 <a class="header-anchor" href="#发布阶段" aria-label="Permalink to &quot;发布阶段&quot;">​</a></h2><ul><li>规范流程</li><li>持续集成</li><li>容灾备份</li></ul><h2 id="渲染阶段优化" tabindex="-1">渲染阶段优化 <a class="header-anchor" href="#渲染阶段优化" aria-label="Permalink to &quot;渲染阶段优化&quot;">​</a></h2><ul><li>合理使用缓存</li><li>合理安排资源的加载顺序</li><li>预先解析、预先加载、预先渲染</li><li>懒加载、懒执行、懒销毁</li></ul>',14),d=[t];function r(c,s,n,h,_,u){return i(),a("div",null,d)}const m=l(o,[["render",r]]);export{b as __pageData,m as default};
